import React from 'react';
import { ExampleCardProps } from './types';
import { cardStyles } from './styles';
import { cn } from '@/lib/utils';

export const ExampleCard: React.FC<ExampleCardProps> = ({ title, description, variant = 'default' }) => {
    return (
        <div className={cn(cardStyles.container, cardStyles.variants[variant])}>
            <h3 className={cardStyles.title}>{title}</h3>
            <p className={cardStyles.description}>{description}</p>
        </div>
    );
};

export const cardStyles = {
    container: "rounded-xl p-6 transition-all duration-200",
    variants: {
        default: "bg-surface shadow-sm",
        outlined: "border border-border bg-transparent"
    },
    title: "text-lg font-semibold text-fg-primary mb-2",
    description: "text-sm text-fg-secondary"
};

export interface ExampleCardProps {
    title: string;
    description: string;
    variant?: 'default' | 'outlined';
}
