@theme {
  /* Fonts */
  --font-sans: "Geist", ui-sans-serif, system-ui, sans-serif;
  --font-serif: "Geist", ui-serif, Georgia, serif;
  --font-mono: "Geist Mono", ui-monospace, SFMono-Regular, monospace;

  /* Font Sizes */
  --font-size-2xs: var(--typography-2xs);
