  --base-static-black: oklch(0 0 0);
  --base-static-white: oklch(1 0 0);
  --base-transparent: transparent;
  --bg-accent: var(--neutral-100);
  --bg-background: var(--neutral-0);
  --bg-canvas: var(--neutral-50);
  --bg-critical: var(--critical-500);
  --bg-critical-hover: var(--critical-600);
  --bg-critical-muted: var(--critical-50);
  --bg-critical-subtle: var(--critical-50);
  --bg-elevated-0: var(--neutral-0);
