  --border-default: var(--neutral-200);
  --border-info: var(--info-500);
  --border-inverted: var(--neutral-950);
  --border-muted: var(--neutral-100);
  --border-primary: var(--brand-600);
  --border-secondary: var(--neutral-200);
  --border-strong: var(--neutral-300);
  --border-success: var(--success-500);
  --border-warning: var(--warning-500);
  --brand-100: oklch(0.95 0.03 155);
  --brand-200: oklch(0.88 0.07 155);
  --brand-300: oklch(0.79 0.11 155);
  --brand-400: oklch(0.70 0.14 155);
  --brand-50: oklch(0.97 0.02 155);
